mapper.update:
UPDATE BOARD_BASE
SET BOARD_LIKE = BOARD_LIKE + 1
WHERE BOARD_NO = #{board_no}

UPDATE BOARD_BASE
SET BOARD_LIKE = BOARD_LIKE - 1
WHERE BOARD_NO = #{board_no}

UPDATE BOARD_BASE
SET
BOARD_CONTENT = #{board_content}
WHERE
BOARD_NO=#{board_no}

mapper.delete:
DELETE FROM LIKE_BASE
WHERE BOARD_NO = #{board_no} AND
REG_ID = #{reg_id};

mapper.update:
UPDATE FILE_BASE
SET USE_YN = 'N'
WHERE FILE_NO = #{fileNo}

mapper.select:
SELECT * FROM 
LIKE_BASE WHERE 
BOARD_NO = #{board_no} AND REG_ID=#{reg_id}
