mapper.update:
		UPDATE SCHEDULE_BASE SET
  		EMP_NO=#{emp_no}, SCHEDULE_START_TIME=#{schedule_start_time}, SCHEDULE_END_TIME=#{schedule_end_time}, SCHEDULE_CONTENTS=#{schedule_contents}, SCHEDULE_TITLE=#{schedule_title}, SCHEDULE_KIND=#{schedule_kind},MOD_ID=#{mod_id},MOD_DATE=sysdate()
  		WHERE SCHEDULE_NO=#{schedule_no}

UPDATE SCHEDULE_BASE SET
USE_YN='N'
WHERE SCHEDULE_NO=#{schedule_no}
